C++<pre><code>#include &lt;iostream&gt;
#include &lt;vector&gt;

using namespace std;

class Solution {
public:
int m, n;
int len;

bool dfs(int x, int y, vector&lt;vector&lt;char&gt;&gt; &amp;board, int idx, string &amp;word,
vector&lt;vector&lt;bool&gt;&gt; &amp;visited) {
if (idx == len) return true;

for (int i = -1; i &lt;= 1; ++i) {
if (x + i &lt; 0 || x + i &gt;= m) continue;

for (int j = -1; j &lt;= 1; ++j) {
if (i == 0 &amp;&amp; j == 0) continue; // 本身
if(i==j || i==-1) continue; //四角
if (y + j &lt; 0 || y + j &gt;= n) continue; // 越界
if (visited[x + i][y + j]) continue; // 前置节点
if (board[x + i][y + j] == word[idx]) {
visited[x + i][y + j] = true;
int ret = dfs(x + i, y + j, board, idx + 1, word, visited);
if(ret) return true;
}
}
}
visited[x][y] = false;
return false;
}

bool exist(vector&lt;vector&lt;char&gt;&gt; &amp;board, string word) {
m = board.size();
n = board[0].size();
len = word.size();

vector&lt;vector&lt;bool&gt;&gt; visited(m, {vector&lt;bool&gt;(n, false)});

char ch = word[0];

for (int i = 0; i &lt; m; ++i) {
for (int j = 0; j &lt; n; ++j) {
if (board[i][j] == ch) {
visited[i][j] = true;
bool ret = dfs(i, j, board, 1, word, visited);
if (ret) return true;
}
}
}

return false;
}
};

int main() {
vector&lt;vector&lt;char&gt;&gt; test = {
{'A', 'B', 'C', 'E'}, {'S', 'F', 'C', 'S'}, {'A', 'D', 'E', 'E'}};

string word = "ABCCED";

Solution s;

cout &lt;&lt; s.exist(test, word);
</code></pre>

